mapper.select:
SELECT user_id, user_type_id
FROM users
WHERE user_id = #{user_id}

mapper.update:
UPDATE books
SET book_quantity = book_quantity - 1
WHERE book_isbn = #{book_isbn}

UPDATE books
SET book_quantity = book_quantity + 1
WHERE book_isbn = #{book_isbn}

UPDATE book_types
SET book_type_content = #{book_type_content}
WHERE book_type_id = #{book_type_id}
