mapper.select:
SELECT user_id
FROM users
WHERE user_id = #{user_id}

SELECT user_id
FROM users
WHERE user_phone = #{user_phone}

SELECT user_salt
FROM users
WHERE user_id = #{user_id}

SELECT user_id, user_pw, user_name, user_phone, question_id, question_answer, user_salt, user_point, user_type_id, user_type_content, DATE_FORMAT(checkout_date,'%Y-%m-%d %T') AS "checkout_date",user_accesstoken, user_refreshtoken
FROM users NATURAL JOIN user_types
WHERE user_id = #{user_id}

mapper.update:
UPDATE users
user_name = #{new_user_name}, question_id = #{new_question_id}, question_answer = #{new_question_answer}, user_pw = #{new_user_pw}, user_phone = #{new_user_phone}, user_salt = #{new_user_salt},
WHERE user_id = #{user_id}
